import React from "react";
import { NavLink, useNavigate } from "react-router-dom";
import "./Sidebar.css";

function Sidebar({ onLogout }) {
  const navigate = useNavigate();

  const handleLogout = () => {
    localStorage.setItem("isLoggedIn", "false"); // clear session
    onLogout();
    navigate("/login"); // redirect to login
  };

  return (
    <header className="sidebar-container">
      <h1 className="sidebar-title">Finance Dashboard</h1>
      <nav className="sidebar-nav">
        <NavLink to="/dashboard" className="sidebar-item">üè† Dashboard</NavLink>
        <NavLink to="/debt-portfolio" className="sidebar-item">üí≥ Debt Portfolio</NavLink>
        <NavLink to="/strategy-manager" className="sidebar-item">üìä Strategy Manager</NavLink>
        <NavLink to="/payment-tracker" className="sidebar-item">üí∏ Payment Tracker</NavLink>
        <NavLink to="/budget-manager" className="sidebar-item">üìÇ Budget Manager</NavLink>
        <NavLink to="/financial-goals" className="sidebar-item">üéØ Financial Goals</NavLink>

      </nav>

      {/* ‚úÖ Logout button */}
      <button className="sidebar-logout" onClick={handleLogout}>
        üö™ Logout
      </button>
    </header>
  );
}

export default Sidebar;
