import React, { useState, useEffect } from "react";
import {
  getFinancialGoals,
  addFinancialGoal,
  deleteFinancialGoal,
} from "../services/api";
import "./FinancialGoals.css";

function FinancialGoals() {
  const userId = 1; // ‚úÖ Replace with logged-in user ID dynamically later
  const [goals, setGoals] = useState([]);
  const [form, setForm] = useState({
    goalName: "",
    goalType: "SAVINGS",
    targetAmount: "",
    currentAmount: "",
    targetDate: "",
    userId: userId,
  });

  useEffect(() => {
    fetchGoals();
  }, []);

  const fetchGoals = async () => {
    try {
      const data = await getFinancialGoals(userId);
      setGoals(data);
    } catch (err) {
      console.error("Error fetching goals:", err);
    }
  };

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await addFinancialGoal(form);
      fetchGoals(); // refresh list after adding
      setForm({
        goalName: "",
        goalType: "SAVINGS",
        targetAmount: "",
        currentAmount: "",
        targetDate: "",
        userId: userId,
      });
    } catch (err) {
      console.error("Error adding goal:", err);
    }
  };

  const handleDelete = async (id) => {
    try {
      await deleteFinancialGoal(id);
      fetchGoals();
    } catch (err) {
      console.error("Error deleting goal:", err);
    }
  };

  return (
    <div className="goals-container">
      <h2>üéØ Financial Goals</h2>

      {/* Add Goal Form */}
      <form onSubmit={handleSubmit} className="goals-form">
        <input
          type="text"
          name="goalName"
          value={form.goalName}
          onChange={handleChange}
          placeholder="Goal Name"
          required
        />
        <select name="goalType" value={form.goalType} onChange={handleChange}>
          <option value="DEBT_PAYOFF">Debt Payoff</option>
          <option value="EMERGENCY_FUND">Emergency Fund</option>
          <option value="SAVINGS">Savings</option>
          <option value="INVESTMENT">Investment</option>
          <option value="MAJOR_PURCHASE">Major Purchase</option>
          <option value="RETIREMENT">Retirement</option>
        </select>
        <input
          type="number"
          name="targetAmount"
          value={form.targetAmount}
          onChange={handleChange}
          placeholder="Target Amount"
          required


        />
        <input
          type="number"
          name="currentAmount"
          value={form.currentAmount}
          onChange={handleChange}
          placeholder="Current Amount"
        />
        <input
          type="date"
          name="targetDate"
          value={form.targetDate}
          onChange={handleChange}
          required
        />
        <button type="submit">Add Goal</button>
      </form>

      {/* Display Goals */}
      <div className="goals-list">
        {goals.length === 0 ? (
          <p>No goals yet. Add one!</p>
        ) : (
          goals.map((g) => (
            <div key={g.id} className="goal-card">
              <strong>{g.goalName}</strong> ({g.goalType})  
              <br />
              Target: ‚Çπ{g.targetAmount}, Current: ‚Çπ{g.currentAmount}
              <br />
              <small>Target Date: {new Date(g.targetDate).toLocaleDateString()}</small>
              <button onClick={() => handleDelete(g.id)}>‚ùå Delete</button>
            </div>
          ))
        )}
      </div>
    </div>
  );
}

export default FinancialGoals;
